mapper.select:
SELECT DISTINCT bci.* FROM TB_BASE_COMPANY_INFO bci
JOIN TB_REL_COMPANY_INDUSTRY bcis ON bci.CompanyId=bcis.CompanyId
WHERE bci.CompanyId IS NOT NULL
AND bci.CompanyName LIKE CONCAT('%',CONCAT(#{searchCompanyName},'%'))
AND bcis.IndustryId IN
#{item}
AND bci.ScaleCode =#{searchScaleCode}
AND bci.TypeCode=#{searchTypeCode}

SELECT CompanyId, UniqueCode, CompanyName, Area, LegalPerson, ContactWay,( SELECT STUFF(( SELECT ','+dt.DictName FROM TB_REL_COMPANY_INDUSTRY bcis JOIN TB_SYS_DATADICT dt ON bcis.IndustryId=dt.DictId WHERE bcis.CompanyId = bci.CompanyId FOR xml path('') ), 1, 1, '')) as IndustryCode, sd6.DictName as SafeManageRank,sd5.DictName as StandardRank,sd4.DictName as OperatingState, Longt, Lat, sd1.DictName as ScaleCode,sd3.DictName as TypeCode FROM TB_BASE_COMPANY_INFO bci LEFT JOIN TB_SYS_DATADICT sd1 ON bci.ScaleCode = sd1.DictId
LEFT JOIN TB_SYS_DATADICT sd3 ON bci.TypeCode=sd3.DictId LEFT JOIN TB_SYS_DATADICT sd4 ON bci.operatingState=sd4.DictId LEFT JOIN TB_SYS_DATADICT sd5 ON bci.standardRank=sd5.DictId LEFT JOIN TB_SYS_DATADICT sd6 ON bci.safeManageRank=sd6.DictId
WHERE bci.CompanyId=#{companyId}
