mapper.select:
SELECT bci.CompanyId,bci.CompanyName,bci.Longt,bci.Lat, MIN (ISNULL(datediff(day,GETDATE(),bcc.Validity),1)) AS 'CertificateAlarm', (MAX (ISNULL(rad.Status,0))+ COUNT(rad.EquipCode)) AS 'RealtimeAlarm' FROM TB_BASE_COMPANY_INFO bci LEFT JOIN TB_BASE_COMPANY_CERTIFICATE bcc ON bcc.CompanyId=bci.CompanyId LEFT JOIN TB_REALTIME_ALARM_DATA rad ON rad.EquipCode LIKE bci.UniqueCode + '%' WHERE bci.CompanyId IS NOT NULL
AND bci.CompanyName LIKE CONCAT('%',CONCAT(#{searchCompanyName},'%'))
AND bci.ScaleCode =#{searchScaleCode}
AND bci.TypeCode=#{searchTypeCode}
GROUP BY bci.CompanyId,bci.CompanyName,bci.Longt,bci.Lat,rad.EquipCode
